/**
 * Helper function to print test case results
 * @param {Message[]} messages collected messages of a test case
 */
module.exports = (messages) => {
    const topics = []
    const times = []
    const values = []
    const reasons = []
    for (const message of messages) {
        const timeString = (new Date(message.reason[0].timestamp)).toLocaleTimeString()
        console.log(timeString + ' ' + message.topic + ' = ' + message.value + ' [' + message.reason[0].message + ']')
        topics.push(message.topic)
        times.push(timeString)
        values.push(message.value)
        reasons.push(message.reason[0].message)
    }
    console.log(JSON.stringify(topics) + ',')
    console.log(JSON.stringify(values) + ',')
    console.log(JSON.stringify(times) + ',')
    console.log(JSON.stringify(reasons))
}
