'use strict'

const VERBOSE = true
const TestRun = require('@mangar2/testrun')
const Parse = require('../parse.js')
const Tokenizer = require('../tokenizer')

const testRun = new TestRun(VERBOSE)

testRun.on('prepare', (testCase) => {
})

testRun.on('break', (test, decision) => {
})

testRun.on('run', (test, decision) => {
    const tokenizer = new Tokenizer('test', test.input)
    const parse = new Parse(tokenizer)
    const result = parse.getJSON()
    return result
})

testRun.on('validate', (test, result, path) => {
    console.log(JSON.stringify(result, null, 2))
    for (const property in test.expected) {
        const expected = test.expected[property]
        testRun.unitTest.assertDeepEqual(expected, result[property], '[' + property + '] ' + path)
    }
})

function testParse () {
    testRun.run([
        'basics',
        'meta',
        'parameter',
        'class',
        'type'
    ], __dirname)
}

testParse()

testRun.unitTest.showResult(30)
