'use strict'

const MqttClient = require('@mangar2/mqttclient')
const MessageStore = require('@mangar2/messagestore')

// path to your configuration file
const allConfig = require('../../../yahaconfig.json')
const config = require('@mangar2/config')(allConfig)

const mqttClient = new MqttClient(config.messageService)
mqttClient.run()

/**
 * Register messageStore
 */
if (config.messageStore) {
    const messageStore = new MessageStore(config.messageStore)
    messageStore.run()
    mqttClient.registerRecipient(config.messageService.subscriptions, (message) => {
        return messageStore.addMessage(message)
    })
    mqttClient.on('shutdown', async () => {
        messageStore.close()
    })
    console.log('started service messageStore')
}
