'use strict'

const types = require('@mangar2/types')
const { checkRule, Rules, ProcessRule } = require('@mangar2/rules')
const UnitTest = require('@mangar2/unittest')
const unitTest = new UnitTest(false)

const rulesTree =
{
    motion: {
        rules: {
            sleeping: {
                durationWithoutMovementInMinutes: 15,
                allOf: [
                    'ground/motion'
                ],
                allow: [
                    'first/motion'
                ],
                topic: 'system/presence',
                value: 'sleeping'
            },
            awake: {
                durationWithoutMovementInMinutes: 0,
                noneOf: [
                    'ground/motion'
                ],
                topic: 'system/presence',
                value: 'awake'
            },
            absent: {
                type: 'motion',
                durationWithoutMovementInMinutes: 15,
                allOf: [
                    'ground/motion'
                ],
                allow: [
                    'first/motion'
                ],
                topic: 'system/presence',
                value: 'absent'
            }
        }
    }
}

/**
 * checks a property result of a test case
 * @param {string} property name of the property to check
 * @param {Message[]} messages list of result messages to check
 * @param {Object} test test case to check
 */
function checkResult (property, messages, test) {
    if (test[property] !== undefined) {
        const testArray = types.isArray(test[property]) ? test[property] : [test[property]]
        if (messages.length >= testArray.length) {
            for (const index in testArray) {
                const curTest = testArray[index]
                const message = messages[index]
                unitTest.assertEqual(message[property], curTest, test.name)
            }
        } else {
            unitTest.fail('not enough messages provided ' + test.name)
        }
    }
}

/**
 * Runs a list of testcases
 * @param {Object[]} testcases array of tests
 */
function runTest (testcases) {
    for (const testcase of testcases) {
        for (const test of testcase.tests) {
            const checkTime = new Date()
            if (test.time !== undefined) {
                const chunks = test.time.split(':')
                checkTime.setHours(chunks[0], chunks[1])
            }
            const rules = new Rules({ rules: { test: testcase.rule } }, checkRule)
            unitTest.assertEqual(rules.invalidRules.length, 0, 'Invalid test rule found "' + testcase.rule.name + '"')
            if (rules.invalidRules.length > 0) {
                console.log(JSON.stringify(rules.invalidRules[0].messages, null, 2))
            }
            unitTest.assertEqual(rules.rules.length, 1, 'Wrong amount of valid rules provided "' + testcase.rule.name + '"')
            const process = new ProcessRule(test.variables, checkTime)
            const result = process.check(rules.rules[0], test.events)
            unitTest.assertEqual(result.messages.length, test.messages, test.name)
            if (result.messages.length !== test.messages) {
                process.check(testcase.rule, test.events)
            }
            checkResult('topic', result.messages, test)
            checkResult('value', result.messages, test)
            checkResult('QoS', result.messages, test)
        }
    }
}

const rules = new Rules(rulesTree, checkRule)
unitTest.assertEqual(rules.rules.length, 2, 'two legal rules')
unitTest.assertEqual(rules.invalidRules.length, 1, 'one invalid rule')
unitTest.assertEqual(rules.invalidRules[0].messages.type, 'unknown property, misspelled?', 'one invalid rule')

rules.invalidateRule('/motion/sleeping', 'syntax error')
unitTest.assertEqual(rules.rules.length, 1, 'one legal rule')
unitTest.assertEqual(rules.invalidRules.length, 2, 'two invalid rules')
unitTest.assertEqual(rules.invalidRules[1].messages, 'syntax error', 'two invalid rules')

const files = [
    'time',
    'value',
    'variables',
    'require',
    'allow',
    'deny'
]
for (const file of files) {
    runTest(require('./' + file + '.json'))
}

unitTest.showResult(166)
