'use strict'

const DEBUG = false
const types = require('@mangar2/types')
const { checkRule, Rules, ProcessRule } = require('@mangar2/rules')
const UnitTest = require('@mangar2/unittest')
const unitTest = new UnitTest(DEBUG, false)

const rulesTree =
{
    motion: {
        rules: {
            setReceived: {
                anyOf: [
                    '$SYS/presence/set', 'system/presence/set'
                ],
                topic: {
                    '$SYS/presence': ['switch', '$SYS/presence/set', {
                        0: 'absent', 1: 'awake', absent: 'absent', sleeping: 'sleeping', awake: 'awake'
                    }],
                    'system/presence': ['switch', '$SYS/presence/set', {
                        0: 'absent', 1: 'awake', absent: 'absent', sleeping: 'sleeping', awake: 'awake'
                    }],
                    'outdoor/garden/main/switch/lightstairs/blink': ['switch', '$SYS/presence/set', {
                        0: '1', 1: '2', absent: '1', sleeping: '0', awake: '2'
                    },
                    {
                        '$SYS/alarm': 'off'
                    }]
                }
            },
            sleeping: {
                durationWithoutMovementInMinutes: 15,
                allOf: [
                    'ground/motion'
                ],
                allow: [
                    'first/motion'
                ],
                topic: 'system/presence',
                value: 'sleeping'
            },
            awake: {
                durationWithoutMovementInMinutes: 0,
                noneOf: [
                    'ground/motion'
                ],
                topic: 'system/presence',
                value: 'awake'
            },
            absent: {
                type: 'motion',
                durationWithoutMovementInMinutes: 15,
                allOf: [
                    'ground/motion'
                ],
                allow: [
                    'first/motion'
                ],
                topic: 'system/presence',
                value: 'absent'
            }
        }
    }
}

let testNo = 0

/**
 * checks a property result of a test case
 * @param {string} property name of the property to check
 * @param {Message[]} messages list of result messages to check
 * @param {Object} test test case to check
 */
function checkResult (property, messages, test) {
    if (test[property] !== undefined) {
        const testArray = types.isArray(test[property]) ? test[property] : [test[property]]
        if (messages.length >= testArray.length) {
            for (const index in testArray) {
                const curTest = testArray[index]
                const message = messages[index]
                unitTest.assertEqual(message[property], curTest, test.name)
            }
        } else {
            unitTest.fail('not enough messages provided ' + test.name)
        }
    }
}

/**
 * Gets an event list in the right format {topic:date, ...}
 * @param {Array} events array of events
 * @param {Date} date test date/time
 * @returns {Object} event list in right format
 */
function formatEvents (events, date) {
    const result = {}
    if (events !== undefined) {
        for (const event of events) {
            result[event] = date
        }
    }
    return result
}

/**
 * Runs a list of testcases
 * @param {Object[]} testcases array of tests
 */
function runTest (testcases) {
    for (const testcase of testcases) {
        const process = new ProcessRule({}, new Date())
        for (const test of testcase.tests) {
            testNo++
            const checkTime = new Date()
            if (test.time !== undefined) {
                const chunks = test.time.split(':')
                checkTime.setHours(chunks[0], chunks[1], 0, 0)
            }
            const rules = new Rules({ rules: { test: testcase.rule } }, checkRule)
            unitTest.assertEqual(rules.invalidRules.length, 0, 'invalid test rules: "' + testcase.rule.name + '"')
            if (rules.invalidRules.length > 0) {
                console.log(JSON.stringify(rules.invalidRules[0].messages, null, 2))
            }
            unitTest.assertEqual(rules.rules.length, 1, 'Amount of valid rules "' + testcase.rule.name + '"')
            process.variables = test.variables
            process.date = checkTime
            const events = formatEvents(test.events, checkTime)
            const result = process.check(rules.rules[0], events)
            unitTest.assertEqual(result.messages.length, test.messages, testNo + ' ' + test.name)

            checkResult('topic', result.messages, 'topic ' + test)
            checkResult('value', result.messages, 'value ' + test)
            checkResult('QoS', result.messages, 'QoS ' + test)
        }
    }
}

const rules = new Rules(rulesTree, checkRule)
unitTest.assertEqual(rules.rules.length, 2, 'two legal rules')
unitTest.assertEqual(rules.invalidRules.length, 2, 'two invalid rules')

rules.invalidateRule('/motion/sleeping', 'syntax error')
unitTest.assertEqual(rules.rules.length, 1, 'one legal rule')
unitTest.assertEqual(rules.invalidRules.length, 3, 'two invalid rules')
unitTest.assertEqual(rules.invalidRules[2].messages, 'syntax error', 'two invalid rules')

const files = [
    'activation',
    'cooldown',
    'time',
    'value',
    'variables',
    'require',
    'allow',
    'deny'
]
for (const file of files) {
    unitTest.log('File: ' + file)
    runTest(require('./' + file + '.json'))
}

unitTest.showResult(167)
