'use strict'

const MqttClient = require('@mangar2/mqttclient')
const readConfig = require('./configuration')
const { registerService } = require('./registerservice')

const config = readConfig('yahaconfig.json')
const mqttClient = new MqttClient(config.runservices)

mqttClient.run()

registerService(mqttClient, config, config.automation, 'automation', '@mangar2/automation')
registerService(mqttClient, config, config.serialDevice, 'serialDevice', '@mangar2/serialdevice')
registerService(mqttClient, config, config.rs485Interface, 'rs485Interface', '@mangar2/rs485interface')
registerService(mqttClient, config, config.pushover, 'pushover', '@mangar2/pushover')
registerService(mqttClient, config, config.opensensemap, 'opensensemap', '@mangar2/opensensemap')
registerService(mqttClient, config, config.sunnyportal, 'sunnyportal', '@mangar2/sunnyportal')
registerService(mqttClient, config, config.messageStore, 'messageStore', '@mangar2/messagestore')
registerService(mqttClient, config, config.remoteService, 'remoteService', '@mangar2/remoteService')
registerService(mqttClient, config, config.zwave, 'zwave', '@mangar2/zwave')

// only for testing. This is a main program
module.exports = mqttClient
