'use strict'

const MqttClient = require('@mangar2/mqttclient')
const types = require('@mangar2/types')
const fs = require('fs')
const readConfiguration = require('@mangar2/config').readConfiguration
const errorLog = require('@mangar2/errorlog')

// path to your configuration file
const config = readConfiguration('yahaconfig.json')
if (!types.isObject(config.runservices)) {
    errorLog('The active configuration does not contain a "runservice" section, program stopped')
    process.exit(1)
}
const mqttClient = new MqttClient(config.runservices)

/**
 * Registers the automation service, if the configuration demands it
 */
function registerAutomation () {
    if (config.automation && config.runservices.services.includes('automation')) {
        try {
            const Automation = require('@mangar2/automation')
            let rules = config.automation.rules

            if (types.isString(rules)) {
                rules = JSON.parse(fs.readFileSync(rules))
            }
            const automation = new Automation(config.automation, rules)
            console.log('checking automation rules ...')
            const subscriptions = automation.getSubscriptions()

            mqttClient.registerRecipient(subscriptions, (message) => {
                console.log(message)
                automation.setVariableByMessage(message)
                const result = automation.processRules()
                return result.messages
            })
            mqttClient.registerSender(config.automation.intervalInSeconds * 1000, async () => {
                const result = automation.processRules()
                return result.messages
            })

            console.log('started automation service')
        } catch (err) {
            errorLog(err, false)
        }
    }
}

/**
 * Registers the automation service, if the configuration demands it
 */
function registerMotion () {
    if (config.motion && config.runservices.services.includes('motion')) {
        try {
            const Motion = require('@mangar2/motion')
            let rules = config.motion.rules

            if (types.isString(rules)) {
                rules = JSON.parse(fs.readFileSync(rules))
            }
            const motion = new Motion(config.motion, rules)
            console.log('checking motion rules ...')
            const subscriptions = motion.getSubscriptions()

            mqttClient.registerRecipient(subscriptions, (message) => {
                console.log(message)
                motion.processMessage(message)
                const result = motion.processRules()
                return result.messages
            })
            mqttClient.registerSender(config.automation.intervalInSeconds * 1000, async () => {
                const result = motion.processRules()
                return result.messages
            })

            console.log('started motion service')
        } catch (err) {
            errorLog(err, false)
        }
    }
}

registerAutomation()
registerMotion()

mqttClient.run()
