'use strict'

const { sunrise, sunset } = require('@mangar2/sun')
const UnitTest = require('@mangar2/unittest')
const unitTest = new UnitTest(true, true)

unitTest.assertEqual(sunrise(8.203454, 49.9048667, new Date('2020-1-27')).toLocaleString(), '2020-1-27 08:07:52')
unitTest.assertEqual(sunset(8.203454, 49.9048667, new Date('2020-1-27')).toLocaleString(), '2020-1-27 17:12:48')
unitTest.assertEqual(sunset(51.1788, -1.8262, new Date('2000-1-21')).toLocaleString(), '2000-1-21 15:52:50')

unitTest.showResult(3)
