'use strict'

const Testrun = require('@mangar2/testrun')
const VERBOSE = false

const testRun = new Testrun(VERBOSE)

testRun.on('break', () => {
    testRun.unitTest.log('break')
})

testRun.on('prepare', (testcase) => {
    testRun.unitTest.log('prepare')
    return 'prepared'
})

testRun.on('run', (test, testObject) => {
    testRun.unitTest.log('run')
    return { testObject, path: test.path }
})

testRun.on('validate', (test, result, path) => {
    testRun.unitTest.log('validate')
    const { testObject } = result
    testRun.unitTest.assertEqual(test.path, path, path)
    testRun.unitTest.assertEqual(result.path, path, path)
    testRun.unitTest.assertEqual(testObject, 'prepared', path)
    if (test.valid === true) {
        testRun.unitTest.assertTrue(test.valid, path)
    } else {
        testRun.unitTest.assertFalse(test.valid, path)
        // run again on error to debug
        testRun.runAgain()
    }
})

testRun.run(['testcase'], __dirname)

testRun.unitTest.showResult(16)
