'use scrict'

const { UnitTest } = require('@mangar2/unittest')

const { readConfiguration } = require('../dist/index')

module.exports = () => {
    const unitTest = new UnitTest(false)
    const config = readConfiguration('@mangar2/utils/test/test-config-file.json')
    unitTest.assertEqual(config.test, 'ok')
    return unitTest.getResultFunctions(1)
}

