'use strict'

const { errorLog, delay, TaskQueue } = require('../index.js')

const testTaskQueue = async () => {
    const taskQueue = new TaskQueue(1000)
    taskQueue.on('task', (info) => console.log("%s: %s", info, (new Date()).toTimeString()))
    taskQueue.addTask('First')
    taskQueue.addTask('Second')
    await delay(3000)
    taskQueue.addTask('Third')
}

testTaskQueue()
