'use strict'

// path to your configuration file
const allConfig = require('../../../yahaconfig.json')
const deviceConfig = require('../../../devices.json')
const yahaConfig = allConfig.test // require('@mangar2/config')(allConfig)
const config = yahaConfig.zwave

const ZwaveController = require('./zwaveController.js')
const zwave = new ZwaveController(config.service, deviceConfig)

const MqttClient = require('@mangar2/mqttclient')
const mqttClient = new MqttClient(config)

const MatchMessages = require('@mangar2/matchmessages')
const matchMessages = new MatchMessages()

mqttClient.on('shutdown', async () => { await mqttClient.close() })
mqttClient.run()

zwave.on('publish', (message) => {
    const matchedMessage = matchMessages.matchAndUpdateReplyMessage(message)
    mqttClient.publish(matchedMessage, config.service.qos, config.service.retain)
})

mqttClient.registerRecipient(config.service.subscriptions, (message) => {
    matchMessages.addReceivedMessage(message)
    return zwave.setValue(message.topic, message.value)
})
