'use strict'

/**
 * @private
 * @description Standard header components for application/json messages
 */
const standardHeaderJSON = {
    'content-type': 'application/json; charset=UTF-8',
    accept: 'application/json,text/plain',
    'accept-charset': 'UTF-8'
}

/**
 * @private
 * @description Standard header components for text/plain messages
 */
const standardHeaderText = {
    'content-type': 'text/plain; charset=UTF-8',
    accept: 'application/json,text/plain',
    'accept-charset': 'UTF-8'
}

/**
 * @private
 * @description Creates the objects to disconnect from a broker
 * @param {string} clientId unique client identifier
 * @returns {Object} {headers, payload, resultCheck(result)}
 */
const disconnect = {
    '0.0': (clientId) => {
        const payload = {
            clientId
        }
        const headers = { ...standardHeaderText, version: '0.0' }

        const resultCheck = (result) => {
            return result.statusCode === 204
        }

        return { headers, payload, resultCheck }
    },

    '1.0': (clientId) => {
        const payload = {
            clientId
        }
        const headers = { ...standardHeaderJSON, version: '1.0' }

        const resultCheck = (result) => {
            return result.statusCode === 204
        }

        return { headers, payload, resultCheck }
    }
}

/**
 * @private
 * @description creates the return types for a disconnect request
 * @returns {Object} {headers, payload, statusCode}
 */
const onDisconnect = {
    '0.0': () => {
        return {
            headers: { 'content-type': 'text/plain; charset=UTF-8', version: '0.0' },
            payload: '',
            statusCode: 204
        }
    },

    '1.0': () => {
        return {
            headers: { 'content-type': 'application/json; charset=UTF-8', version: '1.0' },
            payload: '',
            statusCode: 204
        }
    }
}

module.exports = { disconnect, onDisconnect }
